    private int equation;
    private double sum;
    private final Double[] listSum = new Double[2];
    private String sumOfStrings = "";
    
    private void panel(String value){
        System.out.println(jLabelResult.getText());
        if("0".equals(jLabelResult.getText())){
            
            jLabelResult.setText(value);
            sumOfStrings=jLabelResult.getText();
        }
        else{
            sumOfStrings+=value;
            jLabelResult.setText(sumOfStrings);
        }
    }
    private void addNumbers(){
       equation = 1;
       calculate();
    }
    
    private void minusNumber(){
        equation = 2;
        calculate();      
    }
    
    private void multiplyNumbers(){
        equation = 3;
        calculate();
    }
    private void divideNumbers(){
        equation = 4;
        calculate();
    }
    private void calculate(){
        if(listSum[0] == 0){
            listSum[0] = Double.parseDouble(jLabelResult.getText());
            jLabelResult.setText("0");
            sumOfStrings = "";
        }
        else if(listSum[0] != 0){
            listSum[1] = Double.parseDouble(jLabelResult.getText());
            if(equation == 1) sum = listSum[0] + listSum[1];
            else if(equation == 2) sum = listSum[0] - listSum[1];
            else if(equation == 3) sum = listSum[0] * listSum[1];
            else if(equation == 4) sum = listSum[0] / listSum[1];
            listSum[0] = 0.0;
            listSum[1] = 0.0;
            sumOfStrings = Double.toString(sum);
            jLabelResult.setText(Double.toString(sum));
        }
    }
    private void sumUp(){
        if(equation == 1) sum = listSum[0] + Double.parseDouble(jLabelResult.getText());
        else if(equation == 2) sum = listSum[0] - Double.parseDouble(jLabelResult.getText());
        else if(equation == 3) sum = listSum[0] * Double.parseDouble(jLabelResult.getText());
        else if(equation == 4) sum = listSum[0] / Double.parseDouble(jLabelResult.getText());
        listSum[0] = 0.0;
        sumOfStrings = Double.toString(sum);
        jLabelResult.setText(Double.toString(sum));
        
    }
   
